using Entities.DTO; using Repository; using Service; using System.Net;  namespace ServiceImpl {     public class ProcessarVideoService : IProcessarVideoService     {          private readonly IServiceBusRepository _repository;         private readonly IBlobStorageRepository _blobStorageRepository;          public ProcessarVideoService(IServiceBusRepository repository, IBlobStorageRepository blobStorageRepository)         {             _repository = repository;
            _blobStorageRepository = blobStorageRepository;
        }          public async Task<HttpStatusCode> ProcessarVideo(List<Tuple<string, FileStream>> videos)         {             foreach (var video in videos)             {                 var enviarVideoRequest = new EnviarVideoRequest(video.Item1);                  //Send File to Blob Storage                 await _blobStorageRepository.SendVideoAsync(/*video.Item2*/);                  await _repository.SendVideoInfoToQueueAsync(enviarVideoRequest);              }              return HttpStatusCode.OK;         }     } } 